import React from 'react'

const ScrollToTop = () => {

  const scroll = () =>
    window.scrollTo(0, 0);
  ;

  return (
    <div className="totop">
      <button className="totop__button" onClick="{scroll}">Scroll to top</button>
    </div>
  )
}

export default ScrollToTop
