import React from 'react'
import Post from '../components/post'
import ScrollingWrapper from '../components/scrollingWrapper.jsx'

import "../index.scss";

class Posts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      isLoaded: false,
    }
  }
  componentDidMount() {
    fetch(`https://jsonplaceholder.typicode.com/posts`, {
    	"method": "GET"
    })
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        posts: json,
      })
    });
  };

  render() {
    var { isLoaded, posts } = this.state;

    if (!isLoaded) {
      return <div className={"site-container"}>Loading...</div>
    } else {
      return (
        <div className={"site-container"}>
        <ScrollingWrapper>
          <Post posts={posts} />
          </ScrollingWrapper>
        </div>
      )
    }
  }
}

export default Posts;
