import React from 'react'
import Post from '../components/post'
import { ThemeProvider } from 'emotion-theming'
import preset from '@rebass/preset'

class Posts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      isLoaded: false,
    };
  }
  componentDidMount() {
    fetch(`https://jsonplaceholder.typicode.com/posts`, {
    	"method": "GET"
    })
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        posts: json,
      })
    });
  }
  render() {
    var { isLoaded, posts } = this.state;

    if (!isLoaded) {
      return <div>Loading...</div>
    } else {
      return (
        <ThemeProvider
  theme={{
    colors: {
      background: 'black',
      primary: 'tomato',
    },
    space: [ 0, 6, 12, 24, 48 ],
    fontSizes: [ 14, 16, 18, 20, 24 ],
    radii: {
      default: 12,
    }
  }}>

          <Post posts={posts} />
        </ThemeProvider>
      )
    }
  }
}

export default Posts;
