import React from 'react'

class ScrollToTop extends React.Component {
    componentDidMount() {
        this._div.scrollTop = 0
    }

    render() {
        return <div ref={(ref) => this._div = ref}><button>Text</button></div>
    }
}

export default ScrollToTop
